import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'anchorLinks',
})
export class AnchorLinksPipe implements PipeTransform {
  references: any = /(\[(\S)*])/gi;

  transform(text: string) {
    return this.parseUrl(text);
  }

  private parseUrl(text: string) {
    // Find/Replace reference links ([1, 2]) in text
    // eslint-disable-next-line @typescript-eslint/prefer-regexp-exec
    if (text.match(this.references)) {
      text = text.replace(
        this.references,
        '<span class="link link--anchor">$1</span>'
      );
    }

    return text;
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { ApiService } from '../../../core/services/api/open-genes-api.service';

interface IResearchTypes {
  [n: number]: any;
}

@Pipe({
  name: 'getResearchStatsById',
  pure: false,
})
export class getResearchStatsById implements PipeTransform {
  private researchStatsObj: IResearchTypes;

  constructor(private apiService: ApiService) {}

  transform(symbol: string): void {
    this.apiService
      .getGeneByHGNCsymbol(symbol)
      .subscribe((gene) => {
        const a = gene?.['researches'];

        this.researchStatsObj = {
          1: a.increaseLifespan.length,
          2: a.ageRelatedChangesOfGene.length,
          3: a.interventionToGeneImprovesVitalProcesses.length,
          4: a.proteinRegulatesOtherGenes.length,
          5: a.geneAssociatedWithProgeriaSyndromes.length,
          6: a.geneAssociatedWithLongevityEffects.length,
        };
      })
      .unsubscribe();
  }
}

import { DatePipe } from '@angular/common';
import { Pipe, PipeTransform } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';

@Pipe({
  name: 'localizedDate',
  pure: false,
})
export class LocalizedDatePipe implements PipeTransform {
  constructor(private translate: TranslateService) {}

  transform(value: any, pattern: string = 'mediumDate'): any {
    const date = new Date(value);
    const datePipe: DatePipe = new DatePipe(this.translate.currentLang);

    try {
      return datePipe.transform(date, pattern);
    } catch (e) {
      console.warn(e);
      return value;
    }
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';

@Pipe({
  name: 'publicationLinksWrap',
})
export class PublicationLinksWrapperPipe implements PipeTransform {
  constructor(private translateService: TranslateService) {}
  references = /\[(\S+)\]/gi;

  transform(wholeText: string, localeStr: string) {
    return this.parseUrl(wholeText, localeStr);
  }

  private parseUrl(wholeText: string, localeStr: string) {
    let result = undefined;
    let urlPrefix = '';
    // Find/Replace reference links ([10.5551/jat.41335]) in text
    const words = wholeText.split(' ');
    const translation = this.translateService.instant(localeStr);

    words.forEach((word, i) => {
      // eslint-disable-next-line @typescript-eslint/prefer-regexp-exec
      if (word.match(this.references)) {
        // Prepend URL by matching link format
        const isDOI = word.match(/10.\S*/g); // 10.1007/978-3-540-46129-6
        const isPMID = word.match(/\d*/g); // 23509288
        const isPMCID = word.match(/PMC\d*/g); // PMC3531190

        if (isDOI) {
          urlPrefix = `href="http://doi.org/`;
        } else if (isPMID) {
          urlPrefix = `href="https://pubmed.ncbi.nlm.nih.gov/`;
        } else if (isPMCID) {
          urlPrefix = `href="http://www.ncbi.nlm.nih.gov/pmc/?term=`;
        } else {
          urlPrefix = `href="http://`;
        }

        // Wrap link in HTML element
        const wrappedInLink = word.replace(
          this.references,
          '<a href="$1" target="_blank" class="link link--publication">%LINK%</a>'
        );
        const prefixedLink = wrappedInLink.replace('href="', urlPrefix);
        words[i] = prefixedLink.replace('%LINK%', translation);
      }
    });

    result = words.join(' ');
    if (result) {
      return result;
    }
    return wholeText;
  }
}

import { Pipe, PipeTransform } from "@angular/core";

@Pipe({
  name: "replace",
})
export class ReplacePipe implements PipeTransform {
  transform(value: any, regexValue: string, replaceValue: string): any {
    const formattedValue = value.toString();
    const regex = new RegExp(regexValue, "g");
    // eslint-disable-next-line @typescript-eslint/no-unsafe-return
    return formattedValue.replace(regex, replaceValue);
  }
}

import { Pipe, PipeTransform } from "@angular/core";
import {
  DomSanitizer,
  SafeHtml,
  SafeResourceUrl,
  SafeScript,
  SafeStyle,
  SafeUrl,
} from "@angular/platform-browser";

@Pipe({
  name: "safe",
})
export class SafePipe implements PipeTransform {
  constructor(protected sanitizer: DomSanitizer) {}

  public transform(
    value: any,
    type: string
  ): SafeHtml | SafeStyle | SafeScript | SafeUrl | SafeResourceUrl {
    switch (type) {
      case "html":
        return this.sanitizer.bypassSecurityTrustHtml(value);
      case "style":
        return this.sanitizer.bypassSecurityTrustStyle(value);
      case "styleUrl":
        // eslint-disable-next-line @typescript-eslint/restrict-template-expressions
        return this.sanitizer.bypassSecurityTrustStyle(`url(${value})`);
      case "script":
        return this.sanitizer.bypassSecurityTrustScript(value);
      case "url":
        return this.sanitizer.bypassSecurityTrustUrl(value);
      case "resourceUrl":
        return this.sanitizer.bypassSecurityTrustResourceUrl(value);
      default:
        throw new Error(`Invalid safe type specified: ${type}`);
    }
  }
}

import { Pipe, PipeTransform } from "@angular/core";

@Pipe({
  name: "splitBy",
})
export class SplitByPipe implements PipeTransform {
  transform(value: string, [separator]): string {
    const splits = value.split(separator);
    if (splits.length > 1) {
      return splits.pop();
    } else {
      return "";
    }
  }
}

import { Pipe, PipeTransform } from "@angular/core";

@Pipe({
  name: "stripTags",
})
export class StripTagsPipe implements PipeTransform {
  transform(value: string): any {
    return value
      .replace(/&#{0,1}[a-z0-9]+;/gim, "") // Замена html entities
      .replace(/<\/?\w(?:[^"'>]|"[^"]*"|'[^']*')*>/gim, "") // Замена html-тегов
      .replace(/^\[/gim, "") // Замена открывающей [
      .replace(/\].$/gim, "."); // Замена закрывающей ]
  }
}

import { Pipe, PipeTransform } from "@angular/core";

@Pipe({
  name: "unixTimeFormatter",
})
export class UnixTimeFormatterPipe implements PipeTransform {
  transform(value: number): any {
    return new Date(value * 1000);
  }
}

import { Pipe, PipeTransform } from "@angular/core";

@Pipe({
  name: "zero",
})
export class ZeroPaddingPipe implements PipeTransform {
  transform(num: string): string {
    if (num.length !== 0) {
      const s = "0000000" + num;
      return s.substr(num.length);
    } else {
      return "";
    }
  }
}

