import { Directive, AfterViewInit, NgZone } from '@angular/core';

@Directive({
  selector: '[appDynamicContentAnchors]',
})
export class DynamicContentAnchorsDirective implements AfterViewInit {
  constructor(private ngZone: NgZone) {}

  // TODO: Use Element Ref instead of getting DOM elements directly
  public ngAfterViewInit(): void {
    this.ngZone.runOutsideAngular(() => {
      setTimeout(() => {
        const anchors = document.getElementsByClassName('link--anchor');
        const referenceList = document.getElementById('reference');

        if (anchors && referenceList) {
          const anchorScroll = () => {
            const destinationOffset = referenceList.offsetTop + 10;
            window.scrollTo(0, destinationOffset);
          };
          for (const [, link] of Object.entries(anchors)) {
            link.addEventListener('click', anchorScroll, false);
          }
        }
      });
    });
  }
}

import { ChangeDetectorRef, Directive, Input, OnDestroy, OnInit } from '@angular/core';
import { GenesListSettings } from '../../components/shared/genes-list/genes-list-settings.model';
import { Genes } from '../models';
import { FilterService } from '../../components/shared/genes-list/services/filter.service';
import { FavouritesService } from '../services/favourites.service';
import { MatSnackBar } from '@angular/material/snack-bar';
import { SnackBarComponent } from '../../components/shared/snack-bar/snack-bar.component';
import { Observable, of, Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';
import { Filter } from '../../components/shared/genes-list/services/filter.model';
import { FilterTypesEnum } from '../../components/shared/genes-list/services/filter-types.enum';

@Directive()
export abstract class GeneTableCardLogic implements OnInit, OnDestroy {
  @Input() item: Genes;
  @Input() isGoTermsMode: boolean;

  public listSettings: GenesListSettings;
  public filters: Filter = this.filterService.filters;
  public filterTypes = FilterTypesEnum;

  protected subscription$ = new Subject();

  protected constructor(
    protected filterService: FilterService,
    protected favouritesService: FavouritesService,
    protected snackBar: MatSnackBar,
    protected cdRef: ChangeDetectorRef
  ) {
  }

  ngOnInit(): void {
    this.updateCurrentFields();
  }

  ngOnDestroy(): void {
    this.subscription$.next();
    this.subscription$.complete();
  }

  /**
   * Update list view on card or table
   */
  protected updateCurrentFields() {
    this.filterService.currentFields
      .pipe(
        takeUntil(this.subscription$)
      )
      .subscribe(
      (fields) => {
          this.listSettings = fields;
          this.cdRef.markForCheck();
        },
        (error) => {
          console.log(error);
          this.cdRef.markForCheck();
        },
      );
  }

  /**
   * get item id and send this to app-genes-list  (for filtering)
   */

  public onApplyFilter(filterType: string, filterValue: number | string): void {
    this.filterService.onApplyFilter(filterType, filterValue);
  }

  /**
   * add and delete from favourites
   */
  public favItem(geneId: number): void {
    if (!this.isFaved(geneId)) {
      this.favouritesService.addToFavourites(geneId);
      this.snackBar.openFromComponent(SnackBarComponent, {
        data: {
          title: 'favourites_added',
          length: '',
        },
        duration: 600,
      });
    }

    this.isFaved(geneId);
  }

  public unFavItem(geneId: number): void {
    if (this.isFaved(geneId)) {
      this.favouritesService.removeFromFavourites(geneId);
      this.snackBar.openFromComponent(SnackBarComponent, {
        data: {
          title: 'favourites_removed',
          length: '',
        },
        duration: 600,
      });
    }

    this.isFaved(geneId);
  }

  public isFaved(geneId: number): Observable<boolean> {
    return of(this.favouritesService.isInFavourites(geneId));
  }

  /**
   * Filters translations
   */
  public getExpressionLocaleKey(expression: number): string {
    const expressionTranslations = new Map([
      [0, 'expression_change_no_data'],
      [1, 'expression_change_decreased'],
      [2, 'expression_change_increased'],
      [3, 'expression_change_mixed'],
    ]);

    return expressionTranslations.get(expression);
  }
}

import { Directive, ElementRef, HostBinding, Input, OnChanges, SecurityContext, SimpleChanges } from '@angular/core';
import { DomSanitizer } from '@angular/platform-browser';

@Directive({
  selector: '[appHighlight]',
})
export class HighlightDirective implements OnChanges {
  @Input('highlightText') searchTerm: string;
  @Input() caseSensitive = false;
  @Input() customClasses = '';

  @HostBinding('innerHtml')
  content: string;
  constructor(private el: ElementRef, private sanitizer: DomSanitizer) {}

  ngOnChanges(changes: SimpleChanges) {
    if (this.el?.nativeElement) {
      if ('searchTerm' in changes || 'caseSensitive' in changes) {
        const text = (this.el.nativeElement as HTMLElement).textContent;
        if (this.searchTerm === '') {
          this.content = text;
        } else {
          const regex = new RegExp(this.searchTerm, this.caseSensitive ? 'g' : 'gi');
          const newText = text.replace(regex, (match: string) => {
            return `<mark class="text-highlight">${match}</mark>`;
          });
          const sanitzed = this.sanitizer.sanitize(SecurityContext.HTML, newText);
          this.content = sanitzed;
        }
      }
    }
  }
}

import {
  Directive,
  EventEmitter,
  HostListener,
  Input,
  NgZone,
  Output,
} from '@angular/core';

enum SwipeDirectionEnum {
  RIGHT = 'RIGHT',
  LEFT = 'LEFT',
  UNKNOWN = '',
}

@Directive({
  selector: '[appSwipeHorizontal]',
  exportAs: 'SwipeDirective',
})
export class SwipeHorizontalDirective {
  @Output() readonly OnSwipeUp = new EventEmitter<void>();
  @Output() readonly OnSwipeRight = new EventEmitter<void>();
  @Output() readonly OnSwipeDown = new EventEmitter<void>();
  @Output() readonly OnSwipeLeft = new EventEmitter<void>();
  @Input() insensitiveDistance = 30;

  private isFiredTouchStart = false;
  private isBlocked = false;
  private lastPosition = 0;
  private position = 0;
  private distance = 0;

  constructor(private readonly zone: NgZone) {}

  private get swipingDirection(): SwipeDirectionEnum {
    return this.distance > this.insensitiveDistance
      ? SwipeDirectionEnum.RIGHT
      : this.distance < -this.insensitiveDistance
      ? SwipeDirectionEnum.LEFT
      : SwipeDirectionEnum.UNKNOWN;
  }

  private static getTouch(event: any): Touch | null {
    return event.touches
      ? event.touches.item(0)
      : event.changedTouches
      ? event.changedTouches.item(0)
      : null;
  }

  private fireSwipe(): void {
    switch (this.swipingDirection) {
      case SwipeDirectionEnum.RIGHT:
        this.OnSwipeRight.emit();
        break;
      case SwipeDirectionEnum.LEFT:
        this.OnSwipeLeft.emit();
        break;
      case SwipeDirectionEnum.UNKNOWN:
      default:
        break;
    }

    this.distance = 0;
  }

  @HostListener(`touchstart`, [`$event`])
  onTouchStart(event: any): void {
    if (this.isBlocked) {
      return;
    }

    this.zone.runOutsideAngular(() => {
      const touch = SwipeHorizontalDirective.getTouch(event);

      if (touch) {
        this.isFiredTouchStart = true;
        this.position = touch.clientX;
        this.distance = 0;
      }
    });
  }

  @HostListener(`touchmove`, [`$event`])
  onTouchMove(event: any): void {
    if (!this.isFiredTouchStart) {
      return;
    }

    this.zone.runOutsideAngular(() => {
      const touch = SwipeHorizontalDirective.getTouch(event);

      if (touch) {
        const position = touch.clientX - this.position;

        this.distance += position - this.lastPosition;
        this.lastPosition = position;
      }
    });
  }

  @HostListener(`document:touchend`)
  onTouchEnd(): void {
    if (!this.isFiredTouchStart) {
      return;
    }

    this.zone.runOutsideAngular(() => {
      this.isBlocked = false;
      this.isFiredTouchStart = false;
      this.position = this.lastPosition = 0;
      this.fireSwipe();
    });
  }
}

import {
  Directive,
  ElementRef,
  EventEmitter,
  HostListener,
  Input,
  NgZone,
  Output,
  Renderer2,
} from '@angular/core';

enum SwipeDirectionEnum {
  UP = 'UP',
  DOWN = 'DOWN',
  UNKNOWN = '',
}

@Directive({
  selector: '[appSwipeVertical]',
  exportAs: 'SwipeVerticalDirective',
})
export class SwipeVeticalDirective {
  @Output() readonly OnSwipeUp = new EventEmitter<void>();
  @Output() readonly OnSwipeRight = new EventEmitter<void>();
  @Output() readonly OnSwipeDown = new EventEmitter<void>();
  @Output() readonly OnSwipeLeft = new EventEmitter<void>();
  @Input() insensitiveDistance = 30;

  private isFiredTouchStart = false;
  private isBlocked = false;
  private lastPosition = 0;
  private position = 0;
  private distance = 0;

  constructor(
    private readonly zone: NgZone,
    private readonly element: ElementRef,
    private renderer: Renderer2
  ) {}

  private get swipingDirection(): SwipeDirectionEnum {
    return this.distance > this.insensitiveDistance
      ? SwipeDirectionEnum.DOWN
      : this.distance < -this.insensitiveDistance
      ? SwipeDirectionEnum.UP
      : SwipeDirectionEnum.UNKNOWN;
  }

  private static getTouch(event: any): Touch | null {
    return event.touches
      ? event.touches.item(0)
      : event.changedTouches
      ? event.changedTouches.item(0)
      : null;
  }

  private fireSwipe(): void {
    switch (this.swipingDirection) {
      case SwipeDirectionEnum.UP:
        this.OnSwipeUp.emit();
        break;
      case SwipeDirectionEnum.DOWN:
        this.OnSwipeDown.emit();
        break;
      case SwipeDirectionEnum.UNKNOWN:
      default:
        break;
    }

    this.distance = 0;
  }

  @HostListener(`touchstart`, [`$event`])
  onTouchStart(event: any): void {
    if (this.isBlocked) {
      return;
    }

    this.zone.runOutsideAngular(() => {
      const touch = SwipeVeticalDirective.getTouch(event);

      if (touch) {
        this.isFiredTouchStart = true;
        this.position = touch.clientY;
        this.distance = 0;
      }
    });
  }

  @HostListener(`touchmove`, [`$event`])
  onTouchMove(event: any): void {
    if (!this.isFiredTouchStart) {
      return;
    }

    this.zone.runOutsideAngular(() => {
      const touch = SwipeVeticalDirective.getTouch(event);

      if (touch) {
        const position = touch.clientY - this.position;
        const height = Number(this.element.nativeElement.offsetHeight);

        this.distance += position - this.lastPosition;
        this.lastPosition = position;

        // TODO: Make the same for horizontal directive and use DRY
        const density = 100;
        if (this.lastPosition > 0) {
          if (height >= this.distance && this.distance >= density) {
            this.renderer.setStyle(
              this.element.nativeElement,
              'max-height',
              `${height - position / this.distance}px`
            );
          }
        } else if (this.lastPosition < 0) {
          this.renderer.setStyle(
            this.element.nativeElement,
            'max-height',
            `100%`
          );
        }
      }
    });
  }

  @HostListener(`document:touchend`)
  onTouchEnd(): void {
    if (!this.isFiredTouchStart) {
      return;
    }

    this.zone.runOutsideAngular(() => {
      this.isBlocked = false;
      this.isFiredTouchStart = false;
      this.position = this.lastPosition = 0;
      this.fireSwipe();
    });
  }
}

import {
  AfterViewInit,
  Directive,
  ElementRef,
  HostListener,
  OnDestroy,
} from '@angular/core';
import { environment } from '../../environments/environment';
import { TranslateService } from '@ngx-translate/core';
import { MatBottomSheet } from '@angular/material/bottom-sheet';
import { HttpClient } from '@angular/common/http';
import { TermsComponent } from '../components/shared/terms/terms.component';
import { Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';

interface Terms {
  [key: string]: string;
}

@Directive({
  selector: '[appTermInfo]',
})
export class TermInfoDirective implements AfterViewInit, OnDestroy {
  private terms: Terms;
  private unsubscribe$ = new Subject();
  private content: string;

  constructor(
    private http: HttpClient,
    private elementRef: ElementRef,
    private translateService: TranslateService,
    private bottomSheet: MatBottomSheet
  ) {}

  ngAfterViewInit(): void {
    this.content = this.elementRef.nativeElement.innerText;
    this.getTermsByLang();
  }

  ngOnDestroy(): void {
    this.unsubscribe$.next();
    this.unsubscribe$.complete();
  }

  getTermsByLang(): void {
    const lang = this.translateService.currentLang;
    this.http
      .get(environment.termsJsonUrl[lang === 'en' ? 0 : 1])
      .pipe(takeUntil(this.unsubscribe$))
      .subscribe((terms: Terms) => {
        this.terms = terms;

        this.findTermAndReplaceIt();
      });
  }

  findTermAndReplaceIt(): void {
    if (!this.terms) {
      return;
    }

    Object.keys(this.terms).forEach((term) => {
      this.content = this.content.replace(
        term,
        `<span class="link link--term">${term}</span>`
      );
    });

    this.elementRef.nativeElement.innerHTML = this.content;
  }

  @HostListener('click', ['$event'])
  showTermInfo(evt): void {
    if (evt.target.className !== 'link link--term') {
      return;
    }

    const term = evt.target.textContent;
    this.bottomSheet.open(TermsComponent, {
      data: {
        term: {
          title: term,
          description: this.terms[term],
        },
      },
    });

    evt.preventDefault();
  }
}

