import { Component, OnInit, OnDestroy, ChangeDetectorRef, ChangeDetectionStrategy } from '@angular/core';
import { Genes } from 'src/app/core/models';
import { FavouritesService } from 'src/app/core/services/favourites.service';
import { TranslateService } from '@ngx-translate/core';
import { ApiService } from '../../core/services/api/open-genes-api.service';
import { EMPTY, Subject } from 'rxjs';
import { switchMap, takeUntil } from 'rxjs/operators';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-favourites',
  templateUrl: './favourites.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class FavouritesComponent implements OnInit, OnDestroy {
  public favouriteGenes: Genes[];
  public genes: Genes[];
  public error: number;
  public isSharedList = false;

  private favouriteGenesIds: number[] = [];
  private subscription$ = new Subject();

  constructor(
    public translate: TranslateService,
    private readonly cdRef: ChangeDetectorRef,
    private readonly apiService: ApiService,
    private favouritesService: FavouritesService,
    private route: ActivatedRoute
  ) {}

  ngOnInit() {
    this.getData();
  }

  ngOnDestroy() {
    this.subscription$.unsubscribe();
  }

  private getData(): void {
    this.favouritesService
      .getItems()
      .pipe(
        switchMap((idList) => {
          if (idList) {
            this.favouriteGenesIds = idList;
            this.cdRef.markForCheck();

            return this.apiService.getGenes();
          }

          return EMPTY;
        }),
        takeUntil(this.subscription$)
      )
      .subscribe(
        (genes) => {
          this.genes = genes;
          const queryParamsId = this.route.snapshot.queryParams.selected;
          const queryParamsIdSplit = queryParamsId?.split(',');
          if (queryParamsId) {
            this.favouriteGenes = genes.filter((gene) => queryParamsIdSplit.includes(gene.id.toString()));
            this.isSharedList = true;
          } else {
            this.favouriteGenes = genes.filter((gene) => this.favouriteGenesIds.includes(gene.id));
          }
          this.cdRef.markForCheck();
        },
        (err) => {
          this.error = err;
        }
      );
  }
}

import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  ElementRef,
  Input,
  ViewChild,
} from '@angular/core';
import { Genes } from '../../../core/models';
import { FavouritesService } from '../../../core/services/favourites.service';
import { FileExportService } from '../../../core/services/file-export.service';
import { SafeResourceUrl } from '@angular/platform-browser';
import { MatSnackBar } from '@angular/material/snack-bar';

@Component({
  selector: 'app-favourites-list',
  templateUrl: './favourites-list.component.html',
  styleUrls: ['./favourites-list.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class FavouritesListComponent {
  public favourites: Genes[] = [];
  public downloadLink: string | SafeResourceUrl = '#';

  @Input() set favouriteGenes(_genes: Genes[]) {
    this.favourites = _genes;
  }
  @Input() isSharedList = false;
  @ViewChild('linkCopy') linkCopied: ElementRef;

  constructor(
    private readonly cdRef: ChangeDetectorRef,
    private favouritesService: FavouritesService,
    private fileExportService: FileExportService,
    protected _snackBar: MatSnackBar
  ) {}

  public unFavItem(geneId: number, index: number): void {
    this.favourites.splice(index, 1);
    this.favouritesService.removeFromFavourites(geneId);
    this.cdRef.markForCheck();
  }

  public clearFavs(): void {
    this.favourites.splice(0, this.favourites.length);
    this.favouritesService.clearFavourites();
    this.cdRef.markForCheck();
  }

  public downloadFavs(): void {
    this.downloadLink = this.fileExportService.downloadJson(this.favourites);
    this.cdRef.markForCheck();
  }

  shareGene() {
    const localStorageFavorites = localStorage.getItem('favourites').slice(1, -1);
    const textSplit = localStorageFavorites.split(',');
    const link = location.href + '?selected=' + textSplit.join();
    // eslint-disable-next-line @typescript-eslint/no-floating-promises
    navigator.clipboard.writeText(link);
    this._snackBar.open(this.linkCopied.nativeElement.textContent, '', {
      duration: 600,
    });
  }
}

